let colors = [];

function setup() {
	rectMode(CENTER)
	createCanvas(windowWidth, windowHeight);
	colors = [76, 46, 7, 52, 89, 17, 96, 25, 72, 12]
	imageMode(CENTER);
	colorMode(HSB, 100)
	setupAudio();
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	noStroke()
	background(0)
	randomSeed(10)

	push()
	var msiz = width

	let loopCount = 36;
	for(let i = 0; i < loopCount; i++) {

		let x = map(i, 0, loopCount - 1, 0, 90);
		msiz -= i * 1.9
		let roundness = 300 - i *4
		var hue = colors[i % colors.length]
		fill(hue, 100, 60);
		stroke(hue, 100,90)
		strokeWeight(16)
		//rect(width / 2, height / 2, msiz, msiz, roundness)
		rect(width / 2, height / 2, msiz, msiz, ampEase)

	}
	pop()

	fill('peachpuff')
	//rect(width / 2, height / 2, 310, 310, 130)
	rect(width / 2, height / 2, msiz, msiz, ampEase)

	fill(0,20)
	//rect(width / 2, height / 2, 310, 310, 130)
	rect(width / 2, height / 2, msiz, msiz, ampEase)

	push()

	let loop = 100
	for(let i = 0; i < loopCount; i++) {
		fill(0, 100 / i * 2.5)
		let centerSize = map(i, 0, loopCount, 100, msiz);
		// let i = map(0,,0,msiz)
		rect(width / 2, height / 2, centerSize, centerSize, ampEase)
		//rect(width / 2, height / 2, 9 * i, 9 * i, 3.7 * i)
	}

	pop()
}

/* AUDIO INIT */
let mic, fftRaw, fft = [],
	waveform = [],
	amp = 0.0,
	ampStereo = {
		l: 0.0,
		r: 0.0
	},
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	userStartAudio();
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average mixed amplitude
	ampStereo.l = mic.amplitude.getLevel(0) * 300; // average left amplitude
	ampStereo.r = mic.amplitude.getLevel(1) * 300; // average right amplitude
	ampEase = ease(amp, ampEase, 0.3); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
