let x = 0;
let y = 0;
let xAxis;
let button;
let soundVar = 0;
let buttonText;


function setup() {
	createCanvas(windowWidth, windowHeight);
	setupAudio();

	slider1 = createSlider(-20, 20, 1, 2); //slider for SpeedE
	slider1.position(width-200, 260)
	slider1.style("transform", "rotate(-90deg)");
	slider1.style('-webkit-appearance', 'none');
	slider1.style('width', '200px');
	slider1.style('background', '#333333');
	slider1.style('outline', 'none');
	slider1.style('height', '5px');

	if(soundVar === 0){
		buttonText = "SOUND ON";
	} else if (soundVar == 1){
		buttonText = "SOUND OFF";
	}

	button = createButton(buttonText)// button for Sound
	button.position(width-141, 90)
	button.style("transform", "rotate(90deg)")
	button.style('background', '#333333');
	button.style("color","#fff");
	button.mouseClicked(dance);
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	background(225);
	stroke(0)

push()
noStroke();
	fill(51)
	textSize(12);
	rotate(radians(90)) // does not work
	text("Geschwindigkeit", width * 0.5+500, 20);
  pop()


	// Fill variable
	let positionX = width * 0.5; // both X in line() has to be the same ot be straight
	let mouseXmap = map(mouseX, 1, width, -0.2, 0.2) // for interactive mouse X
	let mouseYmap = map(mouseY, 1, height, -30, 40) // for interactive mouse Y
	let move = frameCount * 0.01; // Speed for the turning part
	let sound = amp*0.01;

	// reverse color if mouse is pressed
	if(mouseIsPressed){
		background(0);
		stroke(255);
	}

	push()
	noFill();

		beginShape(LINES);
		for(let i = 0; i < 300; i = i + 1) {
			if(soundVar == 1){ // Sound Button Active
				x = sin(i / 16 + (move * slider1.value())) * (i * (1 + mouseXmap-(amp/100)) ); // number1 -> Length of tree / number2 -> width of three
			} else if (soundVar === 0) { // Sound Button inactive
				x = sin(i / 16 + (move * slider1.value())) * (i * (1 + mouseXmap/*+sound*/) ); // number1 -> Length of tree / number2 -> width of three
			}

			y = (i * i) / 300 // Number -> Flatness of sin curve
		//	line(positionX, 2.5 * i, positionX + x, height * 0.43 + y + mouseYmap);
			if(soundVar == 1){ // Sound Button Active
				vertex(positionX, (2.5+(amp/100)) * i)
			} else if (soundVar === 0) { // Sound Button inactive
				vertex(positionX, 2.5 * i)
			}
			vertex(positionX + x, height * 0.43 + y + mouseYmap)
			//line(positionX, 2.5 * i, positionX + x, height * 0.43 + y + mouseYmap);
		}
		endShape();
	pop()
}	

