let mySlider, mySecondSlider, myThirdSilder;

function setup() {
	createCanvas(windowWidth, windowHeight);
			mySlider = createSlider(0, width, width/2, 2);
			mySlider.position(width/1.4, height/1.7);
			mySlider.size(170)
			mySlider.style('-webkit-appearance', 'none');
			mySlider.style('background','pink');
			mySlider.style('height', '3px');
			mySlider.style('outline','none');

			mySecondSlider = createSlider(0, 360, 4, 0.5);
			mySecondSlider.position(width/1.4, height/1.9);
			mySecondSlider.size(170)
			mySecondSlider.style('-webkit-appearance', 'none');
