			mySecondSlider.style('height', '3px');
			mySecondSlider.style('outline','none');

			myThirdSlider = createSlider(0, 360, 4, 0.5);
			myThirdSlider.position(width/1.4, height/1.8);
			myThirdSlider.size(170)
			myThirdSlider.style('-webkit-appearance', 'none');
			myThirdSlider.style('background', 'pink');
			myThirdSlider.style('height', '3px');
			myThirdSlider.style('outline','none');

	setupAudio();
}

function draw() {
	updateAudio();
	background(255)
	stroke(0)
	strokeWeight(1.3)
	noFill();
	translate(width / 2, height / 2)

	for(let i=0; i < 30; i++){ //LOOP START

		let sizeRes = height/80 //width/103; // adapt size
		let x1 = width/9.5;
		let y1 = height/5.0

		//vertexSquare(x, y, mySize, loopCount, myRandom, myRot)
		vertexSquare(-x1, -y1, i*sizeRes, 4, 0, 0); //upper right
		//print(amp/5)
		if(i == 15){
			vertexSquare(x1, -y1, i*sizeRes, 4, 0, mySecondSlider.value());// upper right
		} else {
			vertexSquare(x1, -y1, i*sizeRes, 4, 0, 0);// upper right
		}
		if(i == 8 || i == 2){

			vertexSquare(-x1, y1, i*sizeRes, 4, 0, 3); //lower left
		} else if(i == 7 || i == 12){
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, -10 * myThirdSlider.value()); //lower left
		} else if(i == 1 || i == 5 || i == 16){
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, 8); //lower left
		} else if(i == 5 || i == 3){
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, -26 * myThirdSlider.value()); //lower left // instead of 1, slidername.value()
		} else if(i == 18 || i == 6){
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, -4.7); //lower left
		} else if(i == 2 || i == 26){
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, -5.5 * myThirdSlider.value()); //lower left
		} else if(i == 4 || i == 5){
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, 2); //lower left
		} else if(i == 4 || i == 15 || i == 23){
			push();
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, 9 * myThirdSlider.value()); //lower left
			pop()
		} else {
			vertexSquare(-x1, y1, i*sizeRes, 4, 0, 0) //lower left
		}

		let ampMap = map(amp, 0, 1000, 1, 200)
		fill(248, 159, 252, ampMap)

		//randomSeed(3 + amp)
		randomSeed(mySlider.value())
		vertexSquare(x1, y1, i*sizeRes, 26, 16, 0);// lower right
}

function vertexSquare(x, y, mySize, loopCount, myRandom, myRot){
	push();
	translate(x, y)
	rotate(radians(myRot))
	beginShape();
	for(let i = 0; i < loopCount; i++) {
		let x = map(i, 0, loopCount - 1, -mySize / 2, mySize / 2);
		vertex(x, -mySize / 2 +random(myRandom))
	}
	for(let i = 0; i < loopCount; i++) {
		let x = map(i, 0, loopCount - 1, -mySize / 2, mySize / 2);
		vertex(mySize / 2 +random(myRandom), x)
	}
	for(let i = 0; i < loopCount; i++) {
		let x = map(i, 0, loopCount - 1, mySize / 2, -mySize / 2);
		vertex(x, mySize / 2 +random(myRandom))
	}
	for(let i = 0; i < loopCount; i++) {
		let x = map(i, 0, loopCount - 1, mySize / 2, -mySize / 2);
		vertex(-mySize / 2 +random(myRandom), x)
	}
	endShape(CLOSE);
	pop();
	}
}

